package week1.day1;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub 
			  int i,f=1;  
			  int number=5;    
			  for(i=1;i<=number;i++){    
			      f=f*i;    
			  }    
			  System.out.println("The Factorial of "+number+" is: "+f);    
			 }  


	}



